class Check{                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
    
    public int firstNonRepeating(int arr[], int n) 
    { 
        Map<Integer, Integer> mp = new HashMap<Integer, Integer>();
        
        for(int i=0; i<n; i++)
            if(mp.get(new Integer(arr[i]))==null)
                mp.put(new Integer(arr[i]), 1);
            else
                mp.put(new Integer(arr[i]), mp.get(new Integer(arr[i]))+1);
            
        for(int i=0; i<n; i++){
            if(mp.get(new Integer(arr[i]))==1)
                return arr[i];
        }
        
        return 0;
    }  
    
}
